mapper.select:
SELECT me_id FROM member
WHERE
	me_email=#{user.me_email}
AND
	me_name=#{user.me_name}
LIMIT 1;

SELECT * FROM member
WHERE
	me_authority != '슈퍼 관리자';

mapper.update:
UPDATE member
SET
	me_session_id = #{user.me_session_id},
	me_session_limit = #{user.me_session_limit}
WHERE
	me_id = #{user.me_id}

mapper.select:
SELECT * FROM member
WHERE
	me_session_id = #{me_session_id}
AND
	me_session_limit >= now();
